import { ChainSpecificAddress, Hash256 } from '@l2beat/shared-pure'
import { expect, mockFn, mockObject } from 'earl'
import { BigNumber, ethers, type providers } from 'ethers'

import type { IProvider } from '../../provider/IProvider'
import {
  ConstructorArgsHandler,
  decodeConstructorArgs,
  serializeResult,
} from './ConstructorArgsHandler'

const sampleTxData =
  ''
const sampleAbi = [
  'constructor(string name, string symbol, uint8 decimals, bytes32 someBytes, uint256 someNumber)',
]

describe(ConstructorArgsHandler.name, () => {
  describe(ConstructorArgsHandler.prototype.execute.name, () => {
    it('extracts constructor arguments using heuristic', async () => {
      const handler = new ConstructorArgsHandler(
        'constructorArgs',
        { type: 'constructorArgs' },
        sampleAbi,
      )

      const contractAddress = ChainSpecificAddress.random()
      const txHash = Hash256.random()
      const transaction = fakeEthersTransaction({ data: sampleTxData })

      const provider = mockObject<IProvider>({
        getDeployment: mockFn().resolvesTo({ transactionHash: txHash }),
        getTransaction: mockFn().resolvesTo(transaction),
      })

      const response = await handler.execute(provider, contractAddress)

      expect(response).toEqual({
        field: 'constructorArgs',
        value: [
          'Pi Day N00b Token',
          'PIE',
          18,
          '0xdc03b7993bad736ad595eb9e3ba51877ac17ecc31d2355f8f270125b9427ece7',
          '0',
        ],
      })
      expect(provider.getDeployment).toHaveBeenOnlyCalledWith(contractAddress)
      expect(provider.getTransaction).toHaveBeenOnlyCalledWith(txHash)
    })

    it('names args', async () => {
      const handler = new ConstructorArgsHandler(
        'constructorArgs',
        { type: 'constructorArgs', nameArgs: true },
        sampleAbi,
      )

      const contractAddress = ChainSpecificAddress.random()
      const txHash = Hash256.random()
      const transaction = fakeEthersTransaction({ data: sampleTxData })

      const provider = mockObject<IProvider>({
        getDeployment: mockFn().resolvesTo({ transactionHash: txHash }),
        getTransaction: mockFn().resolvesTo(transaction),
      })

      const response = await handler.execute(provider, contractAddress)

      expect(response).toEqual({
        field: 'constructorArgs',
        value: {
          name: 'Pi Day N00b Token',
          symbol: 'PIE',
          decimals: 18,
          someBytes:
            '0xdc03b7993bad736ad595eb9e3ba51877ac17ecc31d2355f8f270125b9427ece7',
          someNumber: '0',
        },
      })
      expect(provider.getDeployment).toHaveBeenOnlyCalledWith(contractAddress)
      expect(provider.getTransaction).toHaveBeenOnlyCalledWith(txHash)
    })

    it('falls back to extraction with block explorer if heuristic fails', async () => {
      /**
       * You can achieve the same result using:
       * @example
       * ```ts
       * const [ctorFragment] = new ethers.utils.Interface(sampleAbi).fragments
       *
       * const encodedData = ethers.utils.defaultAbiCoder.encode(
       *   ctorFragment!.inputs,
       *   [
       *     'Pi Day N00b Token',
       *     'PIE',
       *     18,
       *     '<your_bytes_bignumber>',
       *     '0',
       *   ]
       *)
       * ```
       */
      const sampleCtorEncodedArgs =
        '' // What would block explorer return

      const handler = new ConstructorArgsHandler(
        'constructorArgs',
        { type: 'constructorArgs' },
        sampleAbi,
      )

      const contractAddress = ChainSpecificAddress.random()

      const provider = mockObject<IProvider>({
        getDeployment: mockFn().rejectsWith('error'), // We could cover the error during decode but any exception within the block will skip the heruistic approach
        getSource: mockFn().resolvesTo({
          constructorArguments: sampleCtorEncodedArgs,
        }),
      })

      const response = await handler.execute(provider, contractAddress)

      expect(response).toEqual({
        field: 'constructorArgs',
        value: [
          'Pi Day N00b Token',
          'PIE',
          18,
          '0xdc03b7993bad736ad595eb9e3ba51877ac17ecc31d2355f8f270125b9427ece7',
          '0',
        ],
      })
      expect(provider.getDeployment).toHaveBeenOnlyCalledWith(contractAddress) // Assert it tried to use heuristic
      expect(provider.getSource).toHaveBeenOnlyCalledWith(contractAddress)
    })
  })
})

describe(decodeConstructorArgs.name, () => {
  it('decodes AirDropToken tx data', () => {
    const iface = new ethers.utils.Interface(sampleAbi)
    const ctor = iface.fragments[0]

    const decoded = decodeConstructorArgs(ctor!, sampleTxData)

    expect([...decoded]).toEqual([
      'Pi Day N00b Token',
      'PIE',
      18,
      '0xdc03b7993bad736ad595eb9e3ba51877ac17ecc31d2355f8f270125b9427ece7',
      ethers.BigNumber.from(0),
    ])
  })

  it('decodes starkware committee tx data', () => {
    const txData =
      ''
    const abi = [
      {
        inputs: [
          {
            internalType: 'address[]',
            name: 'committeeMembers',
            type: 'address[]',
          },
          {
            internalType: 'uint256',
            name: 'numSignaturesRequired',
            type: 'uint256',
          },
        ],
        stateMutability: 'nonpayable',
        type: 'constructor',
      },
    ]
    const iface = new ethers.utils.Interface(abi)
    const ctor = iface.fragments[0]

    const decoded = decodeConstructorArgs(ctor!, txData)

    expect([...decoded]).toEqual([
      [
        '0x696cC7615A50CF12d1d1B38bF18A5606e9708296',
        '0x81165b6504520416487E5b4935865b4D3eeaa6e5',
        '0xA6d068DE0da2Dc1BeCaB509B118CB88723f72b6A',
        '0x0cbb676d12745948f75aF3A172cb7E4A4f8546e8',
        '0xB0d71Ff040A941bB9CA8453044634EebCE5A053D',
        '0x8f3310cc6951AC11F2B125fC8AC2dfA133A9498c',
      ],
      ethers.BigNumber.from(3),
    ])
  })
})

describe('serializeResult', () => {
  it('should serialize a result', () => {
    const results: ethers.utils.Result = [
      ['0x696cC7615A50CF12d1d1B38bF18A5606e9708296'],
      ethers.BigNumber.from(3),
    ]

    const serialized = serializeResult(results)

    expect(serialized).toEqual([
      ['0x696cC7615A50CF12d1d1B38bF18A5606e9708296'],
      '3',
    ])
  })
})

function fakeEthersTransaction(
  defaults: Partial<providers.TransactionResponse> = {},
): providers.TransactionResponse {
  return {
    chainId: 1,
    confirmations: 1,
    data: '0x',
    from: ChainSpecificAddress.random().toString(),
    gasLimit: BigNumber.from(100000),
    hash: Hash256.random().toString(),
    nonce: 1,
    value: BigNumber.from(0),
    wait: () => {
      throw new Error('Not implemented')
    },
    ...defaults,
  }
}
